    var ws;
    var wsUri = "ws:";
    var loc = window.location;
    //console.log(loc);
    if (loc.protocol === "https:") { wsUri = "wss:"; }
    // This needs to point to the web socket in the Node-RED flow
    // ... in this case it's ws/simple
    wsUri += "//" + loc.host + loc.pathname.replace("poco/dashboard/main", "ws/poco");

    function wsConnect() {
        console.log("connect", wsUri);
        ws = new WebSocket(wsUri);
        ws.onmessage = function (msg) {
            atualizaDados(msg);

        document.getElementsByClassName('water-tank')[0].style.visibility = 'visible';
        document.getElementsByClassName('water-tank')[1].style.visibility = 'visible';
        document.getElementsByClassName('water-tank')[2].style.visibility = 'visible';

        }
        ws.onopen = function () {
            //console.log("connected");
        }
        ws.onclose = function () {
            //console.log("not connected");
            setTimeout(wsConnect, 3000);
        }


    }

    function doit(m) {
        if (ws) { ws.send(m); }
    }



