        const machinename = global.get('ctx.setor');
        msg.text = formatSentence(alarmtext) + ' ' + machinename;
        msg.description = formatSentence(alarmtext) + ' ' + machinename;
        msg.value = 1;
    }

    if (alarms_change[alarmtext] == 0) {
        const machinename = global.get('ctx.setor');
        msg.text = null;
        msg.description = formatSentence(alarmtext) + ' ' + machinename;
        msg.value = 0;
    }

}

global.set('ctx.alarms', alarms);

return msg;

function formatSentence(sentence) {
    const words = sentence.split('_');

    const formattedSentence = words.map(word => {
        return word.charAt(0).toUpperCase() + word.slice(1).toLowerCase();
    });
    return formattedSentence.join(' ');
}

