return msg;


function findpos(arrayDeObjetos, textoProcurado) {
  for (let i = 0; i < arrayDeObjetos.length; i++) {
    if (arrayDeObjetos[i].text === textoProcurado) {
      return i; // Retorna a posi√ß√£o do objeto encontrado
    }
  }
  // Retorna -1 se o texto n√£o for encontrado em nenhum objeto
  return false;
}

