function filtrarPorAtributo(arrayDeArrays, valorDoAtributo) {
  // Crie um novo array vazio para armazenar os objetos filtrados
  var objfilter = [];

  // Percorra cada array interno
  for (var i = 0; i < arrayDeArrays.length; i++) {
