    const tree = [];

    data.filter(item => item.parent_id === parentId).forEach(item => {
        const children = buildHierarchy(data, item.id);

            item.icon = '/admin/uimedia/icon/' + formatarTexto(item.type) + '.png';

            item.data = {
              id:item.id,
              text: item.text,
              parent_id: item.parent_id,
              type:item.type,

            };


        if (children.length) {
            item.children = children;
        }

      if (item.type == 'company'){
        item.state = { "opened": true };
      }

        tree.push(item);
    });

    return tree;
}

function formatarTexto(texto) {
  // Converte o texto para min√∫sculas
  var textoMinusculo = texto.toLowerCase();

  // Substitui espa√ßos por underlines
  var textoFormatado = textoMinusculo.replace(/\s+/g, '_');

  return textoFormatado;
}




