function findObjectPathByAttributeValue(obj, attributeName, attributeValue, currentPath = []) {
  for (const key in obj) {
    if (obj.hasOwnProperty(key)) {
      const newPath = [...currentPath, key];

      if (key === attributeName && obj[key] === attributeValue) {
        return newPath;
      } else if (typeof obj[key] === 'object') {
        const nestedPath = findObjectPathByAttributeValue(obj[key], attributeName, attributeValue, newPath);
        if (nestedPath) {
          return nestedPath;
        }
      }
    }
  }
  return false;
}

