function formatarJSON(payload) {
  const hierarquia = [];

  payload.forEach((item, index) => {
    const { company, sector, recipe_sector, product, tag, company_id, recipe_sector_id, sector_id, tag_id, product_id } = item;

    if (!findObjectPathByAttributeValue(hierarquia, "id", company_id)) {
      hierarquia.push({ title: company, id: company_id, content:[] } );
    }

    if (!findObjectPathByAttributeValue(hierarquia, "id", sector_id)) {
      var path = findObjectPathByAttributeValue(hierarquia, "id", company_id);
