function ary2str(value){
    var array = JSON.stringify(value);
    array = array.replace(/\[\[/g, "[");
    array = array.replace(/\]\]/g, "]");
    array = array.replace(/\[/g, "(");
    array = array.replace(/\]/g, ')');
    array = array.replace(/\"/g, '\'');

    return array;
}

